    public static ArrayList<Olshop> getOlshopData() {
        ArrayList<Olshop> listData = new ArrayList<>();
        for (int i = 0; i < nama.length; i++) {
            Olshop Olshop = new Olshop(
                    nama[i],
                    deskripsi[i],
                    logo[i]
            );
            listData.add(Olshop);
        }
        return listData;
    }

